class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(target)
        diff = []
        for i in range(n):
            diff.append(target[i]-nums[i])
        res = 0
        curr = 0
        for i in range(n):
            if i == 0 or (diff[i] * curr >= 0):
                req = abs(diff[i]) - abs(curr)
                if req > 0:
                    res += req
                curr = diff[i]
            else:
                res += abs(diff[i])
                curr = diff[i]
        return res
