class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        prev = diff = cur = ans = 0



        for i in range(n):
            if prev == 0:
                if nums[i] < target[i]:
                    prev = 1
                    diff = target[i] - nums[i]
                    cur += 1
                elif nums[i] > target[i]:
                    prev = -1
                    diff = nums[i] - target[i]
                    cur += 1
                else:
                    continue
            else:
                if (nums[i] < target[i] and prev == 1 and diff == target[i] - nums[i]) or (nums[i] > target[i] and prev == -1 and diff == nums[i] - target[i]):
                    cur += 1
                    continue
                elif nums[i] < target[i] and prev == 1:
                    if target[i] - nums[i] > diff:
                        cur = 1
                        diff = target[i] - nums[i]
                    else:
                        ans += diff - target[i] + nums[i]
                        diff = target[i] - nums[i]
                    continue
                elif nums[i] > target[i] and prev == -1:
                    if nums[i] - target[i] > diff:
                        cur = 1
                        diff = nums[i] - target[i]
                    else:
                        ans += diff + target[i] - nums[i]
                        diff = nums[i] - target[i]
                    continue
                elif nums[i] == target[i]:
                    ans += diff
                    prev = 0
                    cur = 0
                    diff = 0
                else:
                    ans += diff
                    if nums[i] < target[i]:
                        cur += 1
                        prev = 1
                        diff = target[i] - nums[i]

                    else:
                        prev = -1
                        cur += 1
                        diff = nums[i] - target[i]


        return ans + diff
