class Solution {
public:
    using lli = long long;


    long long minimumOperations(vector<int>& v, vector<int>& t) {
        lli n = size(v);
        vector<lli> d(n);

        for (int i=0; i<n;i++) {
            d[i]=t[i]-v[i];
        }

        lli ans=0,k=0;

        for (int i=0;i<n;i++) {
            if (i == 0 || (d[i - 1]*d[i] > 0)) {
                int req = abs(d[i])-abs(k);
                if (req>0) {
                    ans+=req;
                }
                k=d[i];
            } 
            else {
                ans+=abs(d[i]);
                k=d[i];
            }
        }

        return ans;
    }
};
