class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int ops = 0;
        long long ans = 0;
        for (int i = 0; i < nums.size(); i++) {
            int here = min(abs(target[i] - nums[i]), abs(target[i] - (nums[i] + ops)));

            if ((target[i] <= nums[i] + ops && target[i] >= nums[i])) here = 0;
            if ((target[i] >= nums[i] + ops && target[i] <= nums[i])) here = 0;
            ans += here;
            // ans += min(abs(target[i] - nums[i]), abs(target[i] - (nums[i] + ops)));
            // cout << ans << ops << nums[i] << target[i] << endl;
            ops = target[i] - nums[i];

        }
        return ans;
    }
};
