class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def split(d):
            q = []
            mq = []
            for n in d:
                if q and n * q[-1] <= 0:
                    mq.append(q)
                    q = []
                q.append(n)
            if q:
                mq.append(q)
            return mq

        def move(x):
            # print('x', x)
            if not x:
                return 0
            if x[0] < 0:
                x = [-n for n in x]
            # print('x', x)
            if len(x) == 1:
                return x[0]

            m = min(x)
            y = [n - m for n in x]
            mq = split(y)
            # print("x, mq", x, mq)
            for q in mq:
                m += move(q)
            return m

        d = [x - y for x, y in zip(nums, target)]
        mq = split(d)
        # print('mq', mq)
        op = 0
        for q in mq:
            op += move(q)
        return op
