func minimumOperations(nums []int, t []int) (ans int64) {
    for i := range t {
        t[i] -= nums[i]
    }
    for i, v := range t {
        ans += int64(abs(v))
        if i > 0 && ((t[i-1] > 0) == (v > 0)) {
            ans -= int64(min(abs(t[i-1]), abs(v)))
        }
    }
    return ans
}

func abs(x int) int {
    if x < 0 {
        return -x
    }
    return x
}
