class Solution {
    public long minimumOperations(int[] nums, int[] t) {
        int n=t.length;
        long ng=0,ps=0,ans=0;
        int[] pre=new int[n];
        for(int i=0;i<n;i++){
            int diff=t[i]-nums[i];
            pre[i]=diff;
        }
        int op=0;
        boolean f1=false;
        for(int i=0;i<n;i++){
            if(pre[i]>0){
                if(!f1){
                    op=0;
                    f1=true;
                }
                if(pre[i]-op>0) ans+=pre[i]-op;
                op=pre[i];
            }
            else{
                if(f1){
                    op=0;
                    f1=false;
                }
                int tmp=-pre[i];

                if(tmp-op>0) ans+=tmp-op;
                op=tmp;
            }
        }

        return ans;
    }
}
