#include <vector>
#include <algorithm>
#include <climits>
#include <cstdlib> // For abs()

class Solution {
public:
    long long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        std::vector<int> diff;
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            diff.push_back(target[i] - nums[i]);
        }

        int index = 0;
        long long sign = diff[0]; // Changed to long long
        int maxi = 0;
        long long result = 0;

        while (index < n) {
            if (sign * diff[index] > 0) {
                maxi = std::max(maxi, std::abs(diff[index]));
                if (std::abs(diff[index]) < maxi) {
                    if (result > LLONG_MAX - (maxi - std::abs(diff[index]))) {
                        // Handle overflow by setting result to LLONG_MAX
                        result = LLONG_MAX;
                    } else {
                        result += (maxi - std::abs(diff[index]));
                    }
                    maxi = std::abs(diff[index]);
                }
            } else if (sign * diff[index] < 0) {
                sign = -sign;
                if (result > LLONG_MAX - maxi) {
                    // Handle overflow by setting result to LLONG_MAX
                    result = LLONG_MAX;
                } else {
                    result += maxi;
                }
                maxi = std::abs(diff[index]);
            } else {
                if (result > LLONG_MAX - maxi) {
                    // Handle overflow by setting result to LLONG_MAX
                    result = LLONG_MAX;
                } else {
                    result += maxi;
                }
                sign = diff[index];
                maxi = std::abs(diff[index]);
            }
            index++;
        }

        if (result > LLONG_MAX - maxi) {
            // Handle overflow by setting result to LLONG_MAX
            result = LLONG_MAX;
        } else {
            result += maxi;
        }

        return result;
    }
};
