class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);

        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long ans = 0;
        int curr = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int a = abs(diff[i]) - abs(curr);
                if (a > 0) {
                    ans += a;
                }
                curr = diff[i];
            } else {
                ans += abs(diff[i]);
                curr = diff[i];
            }
        }

        return ans;
    }
};
