class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [nums[i]-target[i] for i in range(len(nums))]
        ans = 0
        for i,j in enumerate(diff):
            if i==0:
                ans += abs(j)
                continue
            last = diff[i-1]
            if j*last>0:
                if abs(j)>abs(last):
                    ans += (abs(j)-abs(last))
                else:
                    continue
            else:
                ans += abs(j)
        return ans
