class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for(int i=0;i<nums.size();i++){
            nums[i]=nums[i]-target[i];
        }
        vector<int> diffs;
        long long int prev=0;
        for(int i=0;i<nums.size();i++){
            if(nums[i]!=prev){
                diffs.push_back(nums[i]);
                prev=nums[i];
            }
        }
        long long int ans=0,val=0,maxVal=0;
        for(int i=0;i<diffs.size();i++){
            if(diffs[i]==0){
                ans=ans+maxVal;
                maxVal=0;
                val=-1;
            }
            if(diffs[i]<0 && val==1){
                if(abs(diffs[i])>=maxVal){
                    maxVal=abs(diffs[i]);
                }
                else{
                    ans=ans+maxVal-abs(diffs[i]);
                    maxVal=abs(diffs[i]);
                }
            }
            if(diffs[i]>0 &&val==0){
               if(abs(diffs[i])>=maxVal){
                    maxVal=abs(diffs[i]);
                }
                else{
                    ans=ans+maxVal-abs(diffs[i]);
                    maxVal=abs(diffs[i]);
                } 
            }
            if(diffs[i]<0 && val!=1){
                ans=ans+maxVal;
                val=1;
                maxVal=abs(diffs[i]);
            }
            if(diffs[i]>0 && val!=0){
                ans=ans+maxVal;
                val=0;
                maxVal=abs(diffs[i]);
            }

        }
        ans=ans+maxVal;
        return ans;

    }
};
