#define debug(x) if(false){cout << #x << ": ";print(x);cout<<endl;}
typedef long long int ll;
class Solution {
public:
    long long minimumOperations(vector<int>& vals, vector<int>& t) {
        ll n = vals.size();
        ll mx = 0ll, mn = 0ll;
        vector<ll> a(n), b(n);
        for (ll i = 0; i < n; ++i) {
            ll cur = vals[i]-t[i];
            a[i] = b[i] = 0ll;
            if (cur == 0ll) {
                continue;
            }
            if (cur > 0ll) a[i] = cur;
            else b[i] = -cur;
        }
        ll sol = 0ll;
        debug(a);
        debug(b);
        auto solve = [&](vector<ll> &p) -> void {
            ll last = 0ll, mn = 0ll;
            for (ll &v : p) {
                if (v > last) {
                    if (mn == 0ll) {
                        assert(last == 0ll);
                        mn = v;
                    }
                    last = v;
                    continue;
                }
                if (v == last) {
                    if (last == 0ll) {
                        assert(mn == 0ll);
                    }
                    else 
                        assert(mn != 0ll);
                    continue;
                }
                if (v >= mn) {
                    sol += last-v;
                    last = v;
                    continue;
                }
                sol += last-v;
                mn = v;
                last = v;
            }
            sol += last;
        };
        solve(a);
        debug(sol);
        solve(b);
        debug(sol);
        return sol;
    }
};

