/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    let diff = nums.map((num, i) => target[i] - num);
    let res = 0;
    let curr = 0;
    for (let i = 0; i < diff.length; i++) {
        if (i === 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
            let extra = Math.abs(diff[i]) - Math.abs(curr);
            if (extra > 0) {
                res += extra;
            }
            curr = diff[i];
        } else {
            res += Math.abs(diff[i]);
            curr = diff[i];
        }
    }
    return res;
};
