class Solution {
    long long count(vector<int>& v) {
        if(v.size() == 0) return 0;
        long long ans = v[0];
        for(int i = 1; i < v.size(); i++) {
            if(v[i] > v[i-1]) {
                ans += v[i] - v[i-1];
            }
        }
        return ans;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }
        long long ans = 0;
        for(int i = 0; i < n; ) {
            vector<int> v;
            if(diff[i] > 0) {
                while(i < n && diff[i] > 0) {
                    v.push_back(diff[i]);
                    i++;
                }
            } else if(diff[i] < 0) {
                while(i < n && diff[i] < 0) {
                    v.push_back(-diff[i]);
                    i++;
                }
            } else {
                while(i < n && diff[i] == 0) i++;
            }
            ans += count(v);
        }
        return ans;
    }
};
