class Solution(object):
    def minimumOperations(self, nums, target):
        opr = 0
        n = len(nums)
        diff_array = [0] * n
        for i in range(n):
            diff_array[i] = target[i] - nums[i]
        for i in range(0,-1):
            pass
        for i in range(n):
            if diff_array[i] != 0:
                if i > 0 and ((diff_array[i] < 0 and diff_array[i-1] < 0) or (diff_array[i] > 0 and diff_array[i-1] > 0)):
                    if abs(diff_array[i]) > abs(diff_array[i-1]):
                        opr += abs(diff_array[i]) - abs(diff_array[i-1])
                else:
                    opr += abs(diff_array[i])
        return opr
