class Solution{
func minimumOperations(_ a:[Int],_ b:[Int])->Int{
var c=[Int]()
for i in 0..<a.count{

c.append(b[i]-a[i])


}
var d=0
var e=0


for i in 0..<c.count{


if i==0||(c[i-1]>0&&c[i]>0)||(c[i-1]<0&&c[i]<0){
let f=abs(c[i])-abs(e)


if f>0{
d+=f
}
e=c[i]
}


else{
d+=abs(c[i])
e=c[i]
}


}

return d


}


}
