class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        index_left = 0
        index_right = 0
        result = 0
        while index_right < n:
            index_diff = index_right
            minimum_diff = None
            if nums[index_right] > target[index_right]:
                minimum_diff = inf
                while index_right < n and nums[index_right] > target[index_right]:
                    current_diff = nums[index_right] - target[index_right]
                    if minimum_diff > current_diff:
                        minimum_diff = current_diff
                        index_diff = index_right
                    index_right += 1

                result += abs(minimum_diff)
                for index in range(index_left, min(n, index_right)):
                    nums[index] -= minimum_diff

                if index_diff > index_left:
                    index_right = index_left
                else:
                    index_left = index_diff + 1
                    index_right = index_left
            elif nums[index_right] < target[index_right]:
                minimum_diff = -inf
                while index_right < n and nums[index_right] < target[index_right]:
                    current_diff = nums[index_right] - target[index_right]
                    if minimum_diff < current_diff:
                        minimum_diff = current_diff
                        index_diff = index_right
                    index_right += 1

                result += abs(minimum_diff)
                for index in range(index_left, min(n, index_right)):
                    nums[index] -= minimum_diff

                if index_diff > index_left:
                    index_right = index_left
                else:
                    index_left = index_diff + 1
                    index_right = index_left
            else:
                if index_right == index_left:
                    index_left += 1
                    index_right = index_left

            # print(f"{index_left=}, {index_right=}, {result=}, {minimum_diff=}, {nums=}")

        return result
