class Solution {
public:
    long long minNumberOperations(vector<long long>& target) {
        long long ans = target[0];
        for(int i = 1; i < target.size(); i++){
            ans += max((long long)0,target[i]-target[i-1]);
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n);
        long long min_diff = 1e18;
        for(int i = 0 ;i < nums.size(); ++i){
            diff[i] = target[i] - nums[i];
            min_diff = min(min_diff, diff[i]);
        }

        long long ans =  abs(diff[0]);
        for(int i = 1 ; i < n; ++i){
            long long curr = diff[i];
            long long prev = diff[i-1];
            if(curr > 0 and prev > 0){
                ans += max((long long)0, curr-prev);
            }else if(curr < 0 and prev < 0){
                ans += max((long long)0, prev-curr);
            }else{
                ans += abs(curr);
            }
        }



        return ans;
    }
};
