#define ll long long

class Solution {
public:
    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> v(n,0);

        for (int i = 0; i < n; i++) 
        {
            v[i] = target[i]-nums[i];
        }

        ll ans = 0;
        ll curr = 0;

        for (int i = 0; i < n; i++) 
        {
            if (i == 0 || (v[i - 1] > 0 && v[i] > 0) || (v[i - 1] < 0 && v[i] < 0)) {
                ll x = abs(v[i]) - abs(curr);
                if (x > 0) 
                {
                    ans += x;
                }
                curr = v[i];
            } else {
                ans += abs(v[i]);
                curr = v[i];
            }
        }

        return ans;
    }
};
