class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for(int i=0;i<nums.size();++i){
            nums[i]-=target[i];
        }
        // >0, +,-
        int cnt0=0,cnt1=0;
        if(nums[0]<0) cnt0=-nums[0];
        else cnt1=nums[0];
        long long ans=max(cnt0,cnt1);
        for(int i=1;i<nums.size();++i){
            if(nums[i]>0){
                ans+=max(0,nums[i]-cnt1);
                cnt1=nums[i];
                cnt0=0;
            }
            else{
                ans+=max(0,-(cnt0+nums[i]));
                cnt0=-nums[i];
                cnt1=0;
            }
        }
        return ans;
    }
};
