    ll solve(int l, int r, vl &v)
    {
        ll ans = 0;
        int last = 0;
        Fo(i, l, r + 1)
        {
            v[i]=abs(v[i]);
            if (v[i] > last)
            {
                ans += v[i] - last;
            }
            last = v[i];
        }
        return ans;
    }

public:
    long long minimumOperations(vector<int> &num, vector<int> &tar)
    {
        vl v;
        int n = sz(num);
        fo(i, n)
        {
            v.pb(tar[i] - num[i]);
        }
        if(n==1)
            return abs(v[0]);
        int last = 0;
        bool pos = (v[0] > 0 ? 1 : 0);
        ll ans = 0;
        Fo(i, 1, n)
        {
            if (v[i] > 0 != pos)
            {
                // deb2(last,i-1);
                ans += solve(last, i - 1, v);
                last = i;
                pos = !pos;
                if(i==n-1){
                    ans+=abs(v[i]);
                }
            }
            else if (i == n - 1)
            {
                // deb2(last,i);
                ans += solve(last, i, v);
            }
            // deb(ans);
        }

        return ans;
    }
