class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {

        int n = a.size();

        vector<int> nums;

        for(int i=0; i<n; ++i)
        {
            nums.push_back(a[i]-b[i]);
        }

        for(int i=0; i<nums.size(); ++i)
            cout<<nums[i]<<" ";

        cout<<"\n\n";

        cout<<"\n------\n";


        vector<ll> arr;

        ll prev = nums[0];
        arr.push_back(prev);

        if(nums.size() == 1)
            return abs(nums[0]);

        ll ans = 0;

        for(int i=1; i<nums.size(); ++i)
        {

            ll now = nums[i];
            bool stat = false;

            if(now == 0)
            {
                ans+=find(arr);
                arr.clear();

                for(int j = i; j<nums.size(); ++j)
                {
                    if(nums[j] != 0)
                    {
                        arr.push_back(nums[j]);
                        prev = nums[j];
                        i = j;

                        stat = true;
                        break;
                    }
                }
            }

            if(stat)
                continue;

            if(now*prev > 0)
            {
                arr.push_back(now);
            }

            else
            {
                ans += find(arr);

                arr.clear();

                arr.push_back(now);
            }

            prev = now;

        }

        ans += find(arr);




        return ans;

    }
};
