class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long operation = Math.abs(nums[0]-target[0]), change = operation;
        int pos = 0;

        if(target[0]-nums[0] >= 0) pos = 1;
        for(int i=1; i<nums.length; i++) {
            if(target[i] - nums[i] < 0 && pos == 1) {
                operation += nums[i] - target[i];
                change = nums[i] - target[i];
                pos = 0;
                continue;
            }

            if(target[i] - nums[i] >= 0 && pos == 0) {
                operation += target[i] - nums[i];
                change = target[i] - nums[i];
                pos = 1;
                continue;
            }

            if(change >= Math.abs(target[i] - nums[i])) {
                change = Math.abs(target[i] - nums[i]);
                continue;
            } else {
                operation += Math.abs((target[i] - nums[i])) - change;
                change = Math.abs((target[i] - nums[i]));
            }
        }
        return operation;
    }
}
