class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        boolean[] flag = new boolean[n];
        int[] arr = new int[n];
        for(int i = 0; i < n; i++){
            arr[i] = target[i] - nums[i];
            // if(arr[i] >= 0){
            //     flag[i] = true;
            // }
            // arr[i] = Math.abs(arr[i]);
        }
        long sum = 0;
        Stack<Integer> st = new Stack<>();
        st.add(0);
        int i = 0;
        System.out.println(Arrays.toString(arr));
        while(i < n){
            while(i < n && arr[i] >= 0){
                if(arr[i] < st.peek()){
                    while(st.peek() > arr[i]){
                        st.pop();
                    }
                    if(arr[i] != st.peek()){
                        st.add(arr[i]);
                    } 
                } else {
                    sum += arr[i] - st.peek();
                    if(arr[i] != st.peek()) st.add(arr[i]);
                }

                i++;
            }
            st.clear();
            st.add(0);

            while(i < n && arr[i] < 0){
                arr[i] = Math.abs(arr[i]);
                if(arr[i] < st.peek()){
                    while(st.peek() > arr[i]){
                        st.pop();
                    }
                    if(arr[i] != st.peek()){
                        st.add(arr[i]);
                    } 
                } else {
                    sum += arr[i] - st.peek();
                    if(arr[i] != st.peek()) st.add(arr[i]);
                }


                i++;
            }
            st.clear();
            st.add(0);
        }
        return sum;
    }
