class Solution {
public:
    long long minimumOperations(vector<int>& arr , vector<int>& target) {
        int n = arr.size() ;
        int last = 0 ;
        long long ans = 0 ;
        for(int i=0 ; i<n ; i++){
            int diff = (target[i] - arr[i]) ;
            //cout<<"i : "<<i<<" , diff : "<<diff<<endl ;
            if(diff == 0){
                last = 0 ;
                continue ;
            }
            if((diff > 0 && last < 0) || (diff<0 && last>0)){
                ans += abs(diff) ;
                last = diff ;
                //cout<<"diff sign : ans : "<<ans<<" last : "<<last<<endl;
            }
            else{
                if(abs(last) > abs(diff)){
                    last = diff ;
                    continue ;
                }
                else{
                    ans += abs(diff) - abs(last) ;
                    last = diff ;
                }
            }
            //cout<<" , ans :" <<ans<<endl;
        }
        return ans ;
    }
};
