class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        arr = [target[i]-nums[i] for i in range(n)]
        ans = 0
        d = 0
        for i in range(n):
            if arr[i] > 0:
                if arr[i] > d:
                    ans += min(arr[i], arr[i]-d)
                d = arr[i]
            else:
                if arr[i] < d:
                    ans += min(abs(arr[i]), abs(arr[i]-d))
                d = arr[i]
        return ans
