typedef long long ll;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<ll> a(n);
        for(int i = 0; i < n; i++) {
            a[i] = target[i] - nums[i];
        }
        ll ans = 0, x = 0;
        for(int i = 0; i < n; i++) {
            if(a[i]*x <= 0) {
                ans += abs(a[i]);
            }else {
                ans += max(0ll, abs(a[i]) - abs(x));
            }
            x = a[i];
        }
        return ans;
    }
};
