#define ll long long
class Solution {
public:
    bool chk(int p, int a){
        return ((p > 0 && a > 0) || (p < 0 && a < 0));
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0; 
        ll n = nums.size();
        vector<ll> diff(n, 0);
        for (int i = 0; i < n; ++i) {
            diff[i] = nums[i] - target[i];
        }

        ll prev = diff[0];
        for (int i = 1; i < n; ++i) {
            if (chk(prev, diff[i])) {
                if (abs(prev) < abs(diff[i])) {
                    ans += abs(diff[i]) - abs(prev);
                }
            } else {
                ans += abs(diff[i]);
            }
            prev = diff[i];
        }
        ans += abs(diff[0]);

        return ans;
    }
};
