class Solution {
    public void getminadds(int[] nums, int[] target,long[] ans,int start){
        long minadds=Long.MAX_VALUE;
        int end=start,n=nums.length;
        while(end<n)
        {
            if(nums[end]>=target[end])
                break;
            minadds=Math.min(minadds,Math.abs(nums[end]-target[end]));
            end++;
        }
        for(int i=start;i<end;i++)
            nums[i]+=minadds;
        ans[0]+=minadds;
    }
    public void getminsubs(int[] nums, int[] target,long[] ans,int start){
        long minsub=Long.MAX_VALUE;
        int end=start,n=nums.length;
        while(end<n)
        {
            if(nums[end]<=target[end])
                break;
            minsub=Math.min(minsub,Math.abs(nums[end]-target[end]));
            end++;
        }
        for(int i=start;i<end;i++)
            nums[i]-=minsub;
        ans[0]+=minsub;
    }
    public long minimumOperations(int[] nums, int[] target) {
        long[] ans={0};
        int i=0,n=nums.length;
        while(i<n)
        {
            if(nums[i]==target[i])
            {
                i++;
                continue;
            }
            else
            {
                if(nums[i]<target[i])
                    getminadds(nums,target,ans,i);
                else
                    getminsubs(nums,target,ans,i);
                if(nums[i]==target[i])
                    i++;
            }
        }
        return ans[0];
    }
}
