    long long findans(vector<long long>&res){
        long long o= 0,cr = 0;
        for (int i=0; i<res.size(); i++){
            if (i==0 || res[i-1]>0 && res[i]>0 || res[i-1]<0 &&res[i]<0){
                long long mi = abs(res[i])-abs(cr);
                if (mi>0){
                    o+=mi;
                }
            }
            else o+=abs(res[i]);
            cr = res[i];
        }
        return o;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long >res;
        for(int i = 0;i<n;i++){
            res.push_back(nums[i]-target[i]);
        }
        return findans(res);

        // return findans(nums,target,0,n-1)

