class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       long long req=0;
        for(int i=0;i<nums.size();i++){
            while(nums[i]!=target[i]){
               //cout<<i<<" "<<nums[i]<<" "<<target[i]<<endl;
            int inc=i;
            long long mininc=INT_MAX;
            while(inc<nums.size() && nums[inc]<target[inc]){
                 mininc=min(mininc,(long long)target[inc]-nums[inc]);
                inc++;
            }
                if(inc==i){
                    //mtlb dec only

                    int dec=i;
                    long long mindec=INT_MAX;
                    while(dec<nums.size() && nums[dec]>target[dec]){
                        mindec=min(mindec,(long long)nums[dec]-target[dec]);
                        dec++;
                    }

                    int k=i;
                    req+=mindec;
                    while(k<dec){
                        nums[k]-=mindec;
                        k++;
                    }
                }
                else{
                    int k=i;
                    req+=mininc;
                    while(k<inc){
                        nums[k]+=mininc;
                        k++;
                    }
                }   

            }


        }
        return req;
    }
};
