class Solution {
       public long minimumOperations(int[] nums, int[] target) {
           int n = nums.length;
           long p = 0;
           long ans = 0;
           for(int i = 0; i < n; i++){
               long d = (long)nums[i]-target[i];
               if((p>0&&d<0)||(p<0&&d>0)) p = 0;
               if(p>=0&&d>=0&&d>p){
                   ans += d-p;
               }else if(p<=0&&d<=0&&d<p){
                   ans += p-d;
               }

               p = d;
           }
           return ans;
       }
   }
