class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> arr(nums.size(), 0);
        long long res = 0;
        int m = 0;
        for (int i = 0; i < nums.size(); i++) {
            arr[i] = nums[i] - target[i];
        }

        res += abs(arr[0]);
        m = abs(arr[0]);

        for (int i = 1; i < arr.size(); i++) {
            if ((arr[i] > 0 && arr[i - 1] > 0)  || (arr[i] < 0 && arr[i - 1] < 0)) {
                if (abs(arr[i]) > m) {
                    res += abs(arr[i]) - m;
                }

                m = abs(arr[i]);
            } else {
                res += abs(arr[i]);
                m = abs(arr[i]);
            }
        }

        return res;
    }
};
