class Solution(object):
    def minimumOperations(self, nums, target):
        """
        :type nums: List[int]
        :type target: List[int]
        :rtype: int
        """

        diff = []
        for i in range(len(nums)):
            diff.append(target[i] - nums[i])

        ops = 0
        prev = 0
        for i in range(len(diff)):
            if i == 0:
                if diff[i] < 0:
                    ops -= diff[i]
                else:
                    ops += diff[i]

            else:
                if diff[i] == 0:
                    prev = diff[i]
                    continue

                # pos pos
                if 0 <= prev and 0 < diff[i]:
                    # increasing
                    if prev <= diff[i]:
                        ops += (diff[i] - prev)

                # neg neg
                elif prev <= 0 and diff[i] < 0:
                    # decreasing
                    if diff[i] <= prev:
                        ops -= (diff[i] - prev)

                # pos neg or neg pos
                elif (prev * diff[i]) < 0:
                    if diff[i] < 0:
                        ops -= diff[i]
                    else:
                        ops += diff[i]

            # update prev
            prev = diff[i]

        return ops
