class Solution {
public:
    long long a[100001];
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int len = nums.size();
        for (int i = 0; i < len; i++) {
            nums[i] = target[i] - nums[i];
        }
        a[1] = nums[0];
        for (int i = 2; i <= len; i++) {
            a[i] = nums[i - 1] - nums[i - 2];
        }
        long long ans1 = 0,ans2 = 0;
        for (int i = 1; i <= len; i++) {
            if(a[i] >=0) ans1 += a[i];
            else ans2 += a[i];
        }
        ans2 = abs(ans2);
        return max(ans1,ans2);
    }
};
