class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans=0;
        int  n = nums.length;
        int []arr =new int[n];
        for(int i=0;i<n;i++){
            arr[i]=target[i]-nums[i];
        }
        boolean f=true;
        int d=0;
        for(int i=0;i<n;i++){
            if(arr[i]==0){
                d=0;
                f =true;
                continue;
            }
            boolean c = (arr[i]>0)?true:false;
            if( f && c || (f==false   && c==false)){
                if(f){
                    if(arr[i]-d>0){
                        ans+=arr[i]-d;
                    }
                    d = arr[i];
                }else{
                    int t = Math.abs(arr[i]);
                     if(t-d>0){
                        ans+=t-d;
                    }
                    d = t;
                }
            }else{
                if(c){
                    d=arr[i];
                    f = true;
                    ans+=arr[i];
                }else{
                    d =Math.abs(arr[i]);
                    f =false;
                    ans+=d;
                }
            }
            // System.out.println(ans+" "+d+" "+f+" "+c+" "+ i+" "+arr[i]);
        }
        return ans;
    }
}
