class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long[] arr1=new long[nums.length+1];
        long[] arr2=new long[nums.length+1];
        arr1[nums.length]=0;
        arr2[nums.length]=0;
        for(int i=0;i<nums.length;i++){
            if(nums[i]>target[i]){
                arr1[i]=nums[i]-target[i];
            }
            else{
                arr2[i]=target[i]-nums[i];
            }
        }
        long ans=0;
        int cnt=0;
        for(int i=0;i<arr1.length-1;i++){
            if(arr1[i]>arr1[i+1]){
                ans+=arr1[i]-arr1[i+1];
            }
        }
        cnt=0;
        for(int i=0;i<arr1.length-1;i++){
            //System.out.println(arr2[i]);
            if(arr2[i]>arr2[i+1]){
                ans+=arr2[i]-arr2[i+1];
            }
        }
        return ans;
    }
}
