typedef long long ll;
ll f(vector<ll>&v){
    ll ans = v[0],n = v.size();

    ll op = ans;

    for(int i = 1;i<n;i++){
        if(v[i] > op){
            ans += v[i] - op;

            op = v[i];
        }else op = v[i];
    }

    return ans;

}
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0,n = nums.size();

        vector<ll>diff;

        for(int i = 0;i<n;i++)diff.push_back(nums[i] - target[i]);

        for(int i = 0;i<n;i++){
            if(diff[i] == 0){
                int j = i;
                while(j < n && diff[j] == diff[i])j++;
                i = j;
            }

            if(i == n)break;

            vector<ll>d;
            int j = i;

            while(j < n && (diff[i] * 1ll * diff[j]) >= 0){
                if(diff[j] == 0)break;
                d.push_back(abs(diff[j]));
                j++;
            }

            ans += f(d);

            i = j - 1;
        }

        return ans;
    }
};
