class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            nums[i]-=target[i];
        }
        vector<long long>dif(n,0);
        dif[0]=nums[0];
        for(int i=1;i<n;i++)
        {
            dif[i]=nums[i]-nums[i-1];
        }
        long long ans=0,cnt1=0,cnt0=0;
        for(int i=n-1;i>=0;i--)
        {
            if(dif[i]>0)
            {
                if(dif[i]>cnt0)
                {
                    dif[i]-=cnt0;
                    cnt0=0;
                    ans+=dif[i];
                    cnt1+=dif[i];
                }
                else cnt0-=dif[i];
            }
            if(dif[i]<0)
            {
                if(-dif[i]>cnt1)
                {
                    dif[i]+=cnt1;
                    cnt1=0;
                    ans+=-dif[i];
                    cnt0+=-dif[i];
                }
                else cnt1+=dif[i];
            }
        }
        return ans;

    }
};
