class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        vector<int> diff(a.size());
        for (size_t i = 0; i < a.size(); ++i) {
            diff[i] = b[i] - a[i];
        }

        long long totalOps = 0;
        int currSegDiff = 0;

        for (size_t i = 0; i < diff.size(); ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int addOps = abs(diff[i]) - abs(currSegDiff);
                if (addOps > 0) {
                    totalOps += addOps;
                }
                currSegDiff = diff[i];
            } else {
                totalOps += abs(diff[i]);
                currSegDiff = diff[i];
            }
        }
        return totalOps;
    }
};
