class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       long long pos[nums.size()];
       long long neg[nums.size()];
       for (int i = 0; i < nums.size(); i++) {
            long long temp = target[i] - nums[i];
            if (temp > 0) {
                pos[i] = temp;
                neg[i] = 0;
            } else {
                pos[i] = 0;
                neg[i] = -temp;
            }
       } 
       bool isUp = true;
       long long currMax = 0;
       long long currMin = 0;
       long long moves = 0;
       for (int i = 0; i < nums.size(); i++) {
            if (isUp) {
                if (i == 0 || pos[i] > pos[i-1]) {
                    currMax = max(currMax, pos[i]);
                } else if (pos[i] < pos[i-1]) {
                    isUp = false;
                }
            } else {
                if (pos[i] > pos[i-1]) {
                    moves += currMax - currMin;
                    currMin = pos[i-1];
                    isUp = true;
                    currMax = pos[i];
                }
            }
       }
    //    for (int i = 0; i < nums.size(); i++) {
    //     cout << pos[i] << " ";
    //    }
    //    cout << endl;
    //    cout << currMax << endl;
    //    cout << currMin << endl;
    //    cout << moves << endl;
       moves += currMax - currMin;
       isUp = true;
       currMax = 0;
       currMin = 0;
       for (int i = 0; i < nums.size(); i++) {
            if (isUp) {
                if (i == 0 || neg[i] > neg[i-1]) {
                    currMax = max(currMax, neg[i]);
                } else if (neg[i] < neg[i-1]) {
                    isUp = false;
                }
            } else {
                if (neg[i] > neg[i-1]) {
                    moves += currMax - currMin;
                    currMin = neg[i-1];
                    isUp = true;
                    currMax = neg[i];
                }
            }

       }


       return moves + currMax - currMin;
    }
};
