class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff=[j-i for i,j in zip(nums,target)]
        prev=diff[0]
        out=abs(diff[0])
        for i in range(1,len(diff)):
            if (prev>0 and diff[i]>0) or (prev<0 and diff[i]<=0):
                if abs(diff[i])>abs(prev):
                    out+=abs(diff[i])-abs(prev)
            else:
                out+=abs(diff[i])
            prev=diff[i]
        return out
