using ll=long long int;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<ll>dif;
        for(int i=0;i<n;i++)
        dif.push_back(target[i]-nums[i]);

        ll c=abs(dif[0]);
        ll neg=0;
        if(dif[0]<0)
        neg=1;

        ll last=c;
        for(int i=1;i<n;i++)
        {
            ll x=abs(dif[i]);
           if(dif[i]>=0&&neg==1)
           {
               c+=x;
               neg=0;
           }
            else if(dif[i]<0&&neg==0)
            {
                c+=x;
                neg=1;
            }
            else if(last<x)
            {
                c+=x;
                c-=last;
            }
           last=x;
        }
        return c;
    }
};
