class Solution {
public:
    long long minimumOperations(vector<int>& nums,vector<int>& target) {
        int n = nums.size();
        long long curr = target[0] - nums[0];
        long long ans = abs(curr), d1 = curr, d2;

        for (int i = 1;i<n;i++) {
            d2 = target[i] - nums[i];
            if ((d1 > 0 && d2 > 0) || (d1 < 0 && d2 < 0)) {
                int temp = abs(d2) - abs(curr);
                if (temp > 0)   ans += temp;
                curr = d2;
            } else { 
                ans += abs(d2);
                curr = d2;
            }
            d1 = d2;
        }

        return ans;
    }
};
