        vector<int>d(n);
         for (int i = 0; i < n; i++) {
            d[i] = target[i] - nums[i];
        }

        int curr = 0;
         int i=0;
        while(i < n) {
            if (i == 0) {
                int count = abs(d[i]) - abs(curr);
                if (count > 0) {
                    ans =ans+ count;
                }
                curr = d[i];
            } 
            else if((d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)){
                 int count = abs(d[i]) - abs(curr);
                if (count > 0) {
                    ans =ans+ count;
                }
                curr = d[i];
            }
            else {
                ans += abs(d[i]);
                curr = d[i];
            }
            i++;
        }

        return ans;

    }
