from typing import List

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i] - nums[i] for i in range(len(nums))]

        res = 0
        n = len(diff)
        i = 0

        while i < n:
            start = i
            is_negative = diff[i] < 0

            while i < n and (diff[i] < 0) == is_negative:
                i += 1

            subarray = diff[start:i]
            subarray = [abs(x) for x in subarray]

            if subarray:
                res += subarray[0]
                for j in range(1, len(subarray)):
                    if subarray[j] > subarray[j-1]:
                        res += subarray[j] - subarray[j-1]

        return res
