class Solution {
public:
    #define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<ll>v;
        int n=nums.size();
        for(int i=0;i<n;i++){
            v.push_back(target[i]-nums[i]);
        }
        long long ans=abs(v[0]);
        for(int i=1;i<n;i++){
            if((v[i-1]<0 and v[i]>0) or (v[i-1]>0 and v[i]<0)){
                ans+=abs(v[i]);
                continue;
            }
            if(abs(v[i-1])>abs(v[i])) continue;
            ans+= abs(v[i-1]-v[i]);
        }
        return ans;
    }
};
