class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> a(nums.size()+1),b(nums.size()+1);
        int n=nums.size();
        for(int i=0;i<n;i++){
            a[i]=target[i]-nums[i];
        }
        b[0]=a[0];
        for(int i=1;i<n+1;i++){
            b[i]=a[i]-a[i-1];
        }
        long long ans=0;
        for(int i=0;i<n+1;i++){
            if(b[i]>0)ans+=b[i];
        }
        return ans;
    }
};
