public class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int direction = 0;  
        int currentDiff = 0;  
        int operationsCount = 0;  
        long totalOperations = 0;  
        int n = nums.length;

        for (int i = 0; i < n; i++) {
            if (direction == 0) {
                if (nums[i] < target[i]) {
                    direction = 1; 
                    currentDiff = target[i] - nums[i];
                    operationsCount++;
                } else if (nums[i] > target[i]) {
                    direction = -1;  
                    currentDiff = nums[i] - target[i];
                    operationsCount++;
                } else {
                    continue;  
                }
            } else {
                if ((nums[i] < target[i] && direction == 1 && currentDiff == target[i] - nums[i]) ||
                   (nums[i] > target[i] && direction == -1 && currentDiff == nums[i] - target[i])) {
                    operationsCount++;
                    continue;  
                } else if (nums[i] < target[i] && direction == 1) {
                    if (target[i] - nums[i] > currentDiff) {
                        operationsCount = 1;  
                        currentDiff = target[i] - nums[i];
                    } else {
                        totalOperations += (currentDiff - target[i] + nums[i]);
                        currentDiff = target[i] - nums[i];
                    }
                    continue;
                } else if (nums[i] > target[i] && direction == -1) {
                    if (nums[i] - target[i] > currentDiff) {
                        operationsCount = 1;  
                        currentDiff = nums[i] - target[i];
                    } else {
                        totalOperations += (currentDiff + target[i] - nums[i]);
                        currentDiff = nums[i] - target[i];
                    }
                    continue;
                } else if (nums[i] == target[i]) {
                    totalOperations += currentDiff;
                    direction = 0; 
                    operationsCount = 0;  
                    currentDiff = 0;  
                } else {
                    totalOperations += currentDiff;
                    if (nums[i] < target[i]) {
                        direction = 1;  
                        currentDiff = target[i] - nums[i];
                        operationsCount++;
                    } else {
                        direction = -1; 
                        currentDiff = nums[i] - target[i];
                        operationsCount++;
                    }
                }
            }
        }

        return totalOperations + currentDiff;
    }

}
