class Solution {
public:
    long long helper(vector<int>& target,int left,int right){
        int len=right-left+1;
        vector<int> l(len),r(len);
        stack<int> st,sr;
        for(int i=left;i<=right;i++){
            target[i]=abs(target[i]);
            while(!st.empty() && st.top()>=target[i]){
                st.pop();
            }
            if(st.empty()){
                l[i-left]=0;
            }
            else{
                l[i-left]=st.top();
            }
            st.push(target[i]);
        }
        for(int i=right;i>=left;i--){
            target[i]=abs(target[i]);
            while(!sr.empty() && sr.top()>target[i]){
                sr.pop();
            }
            if(sr.empty()){
                r[i-left]=0;
            }
            else{
                r[i-left]=sr.top();
            }
            sr.push(target[i]);
        }
        long long res=0;
        for(int i=0;i<len;i++){
            long long thresh=max(l[i],r[i]);
            long long rem=(long long)(abs(target[i+left]))-thresh;
            res+=rem;
        }
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;i++){
            target[i]-=nums[i];
            cout<<target[i]<<" ";
        }
        cout<<endl;
        int start=0;
        long long res=0;
        for(int i=0;i<n;i++){
            if(start==i && target[i]==0){start++;continue;}
            if((i==n-1)||target[i]<0 && target[i+1]>=0 || target[i]>0 && target[i+1]<=0){
                res+=helper(target,start,i);
                start=i+1;
            }
        }
        return res;
    }
};
